ost" id="creditoptionAdd"><div class="module_content"><fieldset><label for="creditrule_cycletype"><?php print Dyhb::L("周期",'Template/Default/Creditoption',null);?></label><p><input type="radio" name="creditrule_cycletype" id="creditrule_cycletype" value="0" <?php if($oValue['creditrule_cycletype']==0):?>checked<?php endif;?> onclick="document.getElementById('cycletime').style.display = 'none';document.getElementById('rewardnum').style.display = 'none';">&nbsp;<?php print Dyhb::L("一次",'Template/Default/Creditoption',null);?><input type="radio" name="creditrule_cycletype" value="1" <?php if($oValue['creditrule_cycletype']==1):?>checked<?php endif;?> onclick="document.getElementById('cycletime').style.display = 'none';document.getElementById('rewardnum').style.display = '';">&nbsp;<?php print Dyhb::L("每天",'Template/Default/Creditoption',null);?><input type="radio" name="creditrule_cycletype" value="2" <?php if($oValue['creditrule_cycletype']==2):?>checked<?php endif;?> onclick="document.getElementById('cycletime').style.display = '';document.getElementById('rewardnum').style.display = '';">&nbsp;<?php print Dyhb::L("整点",'Template/Default/Creditoption',null);?><input type="radio" name="creditrule_cycletype" value="3" <?php if($oValue['creditrule_cycletype']==3):?>checked<?php endif;?> onclick="document.getElementById('cycletime').style.display = '';document.getElementById('rewardnum').style.display = '';">&nbsp;<?php print Dyhb::L("间隔分钟",'Template/Default/Creditoption',null);?><input type="radio" name="creditrule_cycletype" value="4" <?php if($oValue['creditrule_cycletype']==4):?>checked<?php endif;?> onclick="document.getElementById('cycletime').style.display = 'none';document.getElementById('rewardnum').style.display = '';">&nbsp;<?php print Dyhb::L("不限",'Template/Default/Creditoption',null);?></p><div class="description"><?php print Dyhb::L("可以根据不同的动作设置相应的积分策略",'Template/Default/Creditoption',null);?></div></fieldset><div id="cycletime" style="display: none"><fieldse